import React, { useState } from 'react';

const App = () => {

    const button = document.getElementById('button');

    const [bodyBackground, setBodyBackground] = useState('#EA3A0D');
    const [btnText, setBtnText] = useState('Click Me');
    const [btnBackground, setBtnBackground] = useState('#353dab');

    const btnStyles = {
        backgroundColor : btnBackground,
    };

    const single = () => {
        setBodyBackground('#353dab');
        setBtnText('ðŸ†‘');
        setBtnBackground('#EA3A0D');
    }

    return (
        <>
            <div className='container' style={{backgroundColor : bodyBackground}} onClick={single}>
                <button id='button' style={btnStyles}> {btnText} </button>
            </div>
        </>
    );
}

export default App;
