import React, { useState } from 'react';

const ListItem = (props) => {

    const [line, setLine] = useState(false);

    const lineThrough = {
        textDecoration: 'line-through 2px #ff5735'
    }

    const lineDefault = {
        textDecoration: 'none'
    }

    const checkBtnChecked = {
        backgroundColor: 'rgb(2 249 2)',
        // color : 'rgb(56, 195, 56)',
    }

    const checkBtnDefault = {
        backgroundColor: '#806a95',
        color : 'white',
    }

    const check = () => {
        setLine(true)
    }

    return (
        <>
            <hr/>
            <div className='list-item'>
            <button 
                className='check-btn' 
                onClick={check} 
                style={line ? checkBtnChecked : checkBtnDefault}>
                âœ”
            </button>

            <li style={line ? lineThrough : lineDefault}>
              {props.ListItemvalue} 
             </li>
             <button className='delete-btn' onClick={()=>{props.deleteItems(props.id)}}>
              <i className="far fa-trash-alt"></i>
              </button>     
            </div>
        </>
    );
}

export default ListItem;
