import React, { useState } from 'react';

const App = () => {

    const [greet, setGreet] = useState('Lorem ipsum');
    const [inputValue, setInputValue] = useState('Lorem ipsum');

    const clickEvent = () => {
        setGreet(`Hello ${inputValue}`);
        setInputValue('');
    }

    return (
        <>
            <div className="container">
                <h1>{greet}</h1>
                <input type="text" placeholder='Enter Your Name' value={inputValue}/>
                <button onClick={clickEvent}>Submit</button>
            </div>
        </>
    );
}

export default App;
