import React, { useState } from 'react';

const App = () => {

    const [name, setName] = useState('');
    const [greet, setGreet] = useState('');
    
    const inputEvent = (event) => {
     const value = event.target.value;
      setName(value);
    }

    const submit = () => {
        setGreet(name);
        setName('');
    }

    const block = {
        display : 'block',
    }

    return(
        <>
            <div>
                <h1>Hello {greet}</h1>
                <form></form>
                <input type="text" placeholder='Enter Your Name' style={block} value={name} onChange={inputEvent}/>
                <button onClick={submit}> Submit </button>
            </div>
        </>
    )
}

export default App;
