import React, { useEffect, useState } from 'react';
import ListItems from './ListItems';
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';



const App = () => {

    // fetching data from localStorage
    const localStorageData = () => {
        const list = localStorage.getItem('toDos')

        if(list) {
            return JSON.parse(list);
        }
        else {
            return [];
        }
    }

    
    const [input, setInput] = useState('');
    const [item, setItem] = useState(localStorageData());

    const inputEvent = (event) => {
        setInput(event.target.value);
    }
    
    const addEvent = () => {
        let slicedInput;
        input.length < 27 ? (slicedInput = input) : (slicedInput = `${input.slice(0,27)}...`);
        input !== '' ? setItem([...item,slicedInput]) : alert('Enter an item !');
        setInput('');
    }

    const deleteItems = (id) => {
        console.log('deleted');

        setItem((old) => {
            return old.filter((arr, index) => index !== id)
        });
    }

    // storing data to localStorage
    useEffect(() => localStorage.setItem('toDos', JSON.stringify(item)), [item]);

   
    return (
        <>
            <div className="container">
                <div className="main">
                    <h1>To Do List</h1>
                    <div className="input-and-button">
                        <input type="text" placeholder='Add an item' onChange={inputEvent} value={input}/>
                        <Fab className='add-btn' onClick={addEvent}> <AddIcon /> </Fab>
                        {/* <Fab color="primary" a><AddIcon /></Fab> */}
                        <hr/>
                    </div>
                    <ul>
                        {
                            item.map((value, indx) => {
                                return (
                                    <ListItems 
                                    key={indx}
                                    id={indx}
                                    ListItemvalue={value}
                                    deleteItems={deleteItems}
                                    />                 
                                );
                            })
                        }

                    </ul>
                </div>
            </div>
        </>
    );
}

export default App;
