import React, { useState } from 'react';

const App = () => {

    const [greet, setGreet] = useState('');
    const [inputValue, setInputValue] = useState('Atanu');

    const clickEvent = () => {
        inputValue != ''? setGreet(`Hello ${inputValue}`) : alert('Input field is Blank');
        setInputValue('');
    }

    const changeEvent = (event) => {
        const name = event.target.value;
        return name;
    }

    return (
        <>
            <div className="container">
                <h1>{greet}</h1>
                <input type="text" placeholder='Enter Your Name' value={inputValue} onChange={changeEvent}/>
                <button onClick={clickEvent}>Submit</button>
            </div>
        </>
    );
}

export default App;
