import React, { useState } from 'react';

const App = () => {

    const [input, setInput] = useState('');
    const [item, setItem] = useState('buy an apple');
    let value = '';

    const inputEvent = (event) => {
        setInput(event.target.value);
    }
    
    const addEvent = (event) => {
        value = event.target.value;
        setItem(value);
    }

    // splice up the string
    return (
        <>
            <div className="container">
                <div className="main">
                    <h1>To Do List</h1>
                    <div className="input-and-button">
                        <input type="text" placeholder='Add an item' onChange={inputEvent} value={input}/>
                        <button className='add-btn' onClick={addEvent}> + </button>
                        <hr/>
                    </div>
                    <ul>
                        <li> <button className='check-btn'>âœ”</button> {input}</li><button className='delete-btn'><i className="far fa-trash-alt"></i></button>
                    </ul>
                </div>
            </div>
        </>
    );
}

export default App;
