import React, { useState } from 'react';

const App = () => {

    const button = document.getElementById('button');

    const [bodyBackground, setBodyBackground] = useState('#EA3A0D');
    const [btnText, setBtnText] = useState('Click Me');
    const [btnBackground, setBtnBackground] = useState('#353dab');

    return (
        <>
            <div className='container' style={{backgroundColor : bodyBackground}}>
                <button id='button'>{btnText}</button>
            </div>
        </>
    );
}

export default App;
