import React, { useState } from 'react';

const App = () => {

    const [name, setName] = useState({
        fName : '',
        lName : '',
        email : '',
        mobile : '',
    });
    
    const inputEvent = (event) => {
        const value = event.target.value;
        const name = event.target.name;

        setName((prev) => {
            if(name === 'fName') {
                return ({
                        fName : value,
                        lName : prev.lName,
                        email : prev.email,
                        mobile : prev.mobile,
                    });
            } 
            else if(name === 'lName') {
                return ({
                        fName : prev.fName,
                        lName : value,
                    });
            } else if(name === 'email') {

            }
        });
    }
        
    const submit = (event) => {
        event.preventDefault();
        alert('Form Submitted');
    }

    const block = {
        display : 'block',
    }

    return(
        <>
            <div>
                <h1>Hello {name.fName} {name.lName}</h1>
                <form onSubmit={submit}>
                    <input
                     type="text"
                     placeholder='Enter Your First Name' 
                     name='fName'
                     style={block} 
                     value={name.fname} 
                     onChange={inputEvent}
                     />
                    <input
                     type="text"
                     placeholder='Enter Your Last Name' 
                     name='lName'
                     style={block} 
                     value={name.lName} 
                     onChange={inputEvent}
                     />
                    <input
                     type="text"
                     placeholder='Enter Your Email' 
                     name='email'
                     style={block} 
                     value={name.email} 
                     onChange={inputEvent}
                     />
                    <input
                     type="text"
                     placeholder='Enter Your Mobile No.' 
                     name='mobile'
                     style={block} 
                     value={name.mobile} 
                     onChange={inputEvent}
                     />
                    <button type='submit' > Submit </button>
                </form>
            </div>
        </>
    )
}

export default App;
