import React, { useState } from 'react';

const App = () => {

    const [name, setName] = useState('');

    const inputEvent = (event) => {
      const value = event.target.value;
      setName();
    }

    const block = {
        display : 'block',
    }

    return(
        <>
            <div>
                <h1>Hello {name}</h1>
                <input type="text" placeholder='Enter Your Name' style={block} value={name} onChange={inputEvent}/>
                <button> Submit </button>
            </div>
        </>
    )
}

export default App;
