import React, { useState } from 'react';

const App = () => {

    const [input, setInput] = useState('');
    const [item, setItem] = useState([]);

    const inputEvent = (event) => {
        setInput(event.target.value);
    }
    
    const addEvent = () => {
        let slicedInput;
        input.length < 28 ? (slicedInput = input) : (slicedInput = `${input.slice(0,27)}...`);
        input !== '' ? setItem([...item,slicedInput]) : alert('Enter an item !');
        setInput('');
    }

    // splice up the string
    return (
        <>
            <div className="container">
                <div className="main">
                    <h1>To Do List</h1>
                    <div className="input-and-button">
                        <input type="text" placeholder='Add an item' onChange={inputEvent} value={input}/>
                        <button className='add-btn' onClick={addEvent}> + </button>
                        <hr/>
                    </div>
                    <ul>
                        {
                            item.map((value) => {
                                return (
                                    <>  
                                        <hr/>
                                        <div className='list-item'>
                                        <button className='check-btn'>âœ”</button>
                                        <li>
                                          {value} 
                                         </li>
                                         <button className='delete-btn'>
                                          <i className="far fa-trash-alt"></i>
                                          </button>     
                                        </div>
                                    </>
                                );
                            })
                        }

                    </ul>
                </div>
            </div>
        </>
    );
}

export default App;
